import React, { Component } from 'react';
import { Redirect } from "react-router-dom";

class CreateAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: null,
      email: "",
      password: "",
      userid: null
    };
    this.createAccount = this.createAccount.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.input = React.createRef();
  }
  handleChange(event) {
    this.setState({
      email: event.target.email,
      password: event.target.password
    });
  }
  handleSubmit(event) {
    alert('A name was submitted: ' + this.input.current.value);
    event.preventDefault();
  }
  createAccount() {
    console.log(this.state.email)
    console.log(this.state.password)
    this.setState({ redirect: '/createaccount' });
  }
  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.redirect} />
    }
    return (
      <div>
        <div>Create Account</div>
        <br />
        <form onSubmit={this.handleSubmit}>
            <input type="email" value={this.state.email} onChange={handleChange(event)} ref={this.input} name="email" placeholder="Enter email" />
          <br /><br />
          <label>Password <br />
            <input type="password" value={this.state.password} onChange={this.handleChange} name="password" placeholder="Enter password" />
          </label>
          <br /><br />
          <input type="submit" value="Submit" />
          <button onClick={this.createAccount}>Create Account</button>
        </form>
      </div>
    );
  }
}

export default CreateAccount;
