import React, { Component, createContext } from "react";
import sampleItems from './sampleItems';
import firebase from "firebase/app";
import "firebase/analytics";
import "firebase/auth";
import "firebase/firestore";

var firebaseConfig = {
  apiKey: "AIzaSyCJ-q90-P7x1Bb7qOD7jNutcsjtfwGEgp4",
  authDomain: "gridsystem-7aba2.firebaseapp.com",
  databaseURL: "https://gridsystem-7aba2-default-rtdb.firebaseio.com",
  projectId: "gridsystem-7aba2",
  storageBucket: "gridsystem-7aba2.appspot.com",
  messagingSenderId: "308725097152",
  appId: "1:308725097152:web:ec1491f20548332c734325",
  measurementId: "G-NPY4XSW6H8"
};

var firebaseApp = firebase.initializeApp(firebaseConfig);
var firebaseDatabase = firebaseApp.database();

// Helper functions

function move(array, oldIndex, newIndex) {
  if (newIndex >= array.length) {
    newIndex = array.length - 1;
  }
  array.splice(newIndex, 0, array.splice(oldIndex, 1)[0]);
  return array;
}

function moveElement(array, index, offset) {
  const newIndex = index + offset;

  return move(array, index, newIndex);
}

// Context

const GridContext = createContext({ items: [] });

export class GridProvider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: sampleItems,
      moveItem: this.moveItem,
      setItems: this.setItems
    };
  }
  componentDidMount() {
    this.getGrid();
  }
  getGrid() {
    var gridRef = firebaseDatabase.ref('grids/' + 1);
    gridRef.on('value', (snapshot) => {
      const data = snapshot.val();
      this.setState({ items: data })
      console.log('items state')
      console.log(this.state.items)
    });
  }
  render() {
    return (
      <GridContext.Provider value={this.state}>
        {this.props.children}
      </GridContext.Provider>
    );
  }

  setItems = items => this.setState({ items });

  moveItem = (sourceId, destinationId) => {
    const sourceIndex = this.state.items.findIndex(
      item => item.id === sourceId
    );
    const destinationIndex = this.state.items.findIndex(
      item => item.id === destinationId
    );

    // If source/destination is unknown, do nothing.
    if (sourceId === -1 || destinationId === -1) {
      return;
    }

    const offset = destinationIndex - sourceIndex;

    this.setState(state => ({
      items: moveElement(state.items, sourceIndex, offset)
    }));
  };
}

export default GridContext;
