import React, { Component } from 'react';
import { Redirect } from "react-router-dom";

class CreateAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: null,
      userid: null
    };
    this.createAccount = this.createAccount.bind(this);
  }
  handleChange(event) {
    this.setState({value: event.target.value});
  }
  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }
  createAccount() {
    console.log('createaccount')
    this.setState({ redirect: '/createaccount' });
  }
  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.redirect} />
    }
    return (
      <div>
        <form>
          <label>
            Email:
            <input type="email" value={this.state.email} onChange={this.handleChange} name="email" placeholder="Enter email" />
          </label>
          <label>
            Password:
            <input type="password" value={this.state.password} onChange={this.handleChange} name="password" placeholder="Enter password" />
          </label>
          <input onClick={this.createAccount} type="submit" value="Create Account" />
        </form>
      </div>
    );
  }
}

export default CreateAccount;
