import React, { Component } from 'react';
import { Redirect } from "react-router-dom";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userValid: false,
      redirect: null,
      userid: null
    };
    this.userLoginFunction = this.userLoginFunction.bind(this);
  }
  goToCreateAccount() {
    this.setState({ redirect: '/createaccount' });
  }
  userLoginFunction() {
    console.log("login")
    var userid = 1;
    this.setState({ userValid: true });
    if (this.state.userValid) {
      this.setState({ redirect: `/main/${userid}` });
    } else {
      console.log("user invalid");
    }
  }
  render() {
    return (
      <div>
        <div>Login</div>
        <input placeholder="Enter email" type="text" /><br />
        <input placeholder="Enter password" type="password" /><br /><br />
        <button onClick={this.userLoginFunction}>Log In</button><br />
        <button onClick={this.goToCreateAccount}>Log Out</button>
      </div>
    );
  }
}

export default Login;
